INSERT INTO `post` VALUES ('25', '项目实施工程师', '5000-7000元/月', '[合肥市高新区]百商悦澜山', '两年以上工作经验', '本科学历', '招聘5人', '岗位职责：   1、负责公司实施项目的方案制定；   2、负责公司产品或项目软硬件的部署实施及客户售后支持；   3、组织实施对客户的培训，帮助客户掌握产品相关的安装、使用、维护、管理知识；   4、根据需要参与或独立承担项目的管理工作；   5、根据需要参与或独立承担项目的需求调研、管理工作。      任职资格：   1、本科以上学历，英语水平良好，计算机、通信类相关专业，工作2-3年以上；   2、熟悉Linux操作平台操作，精通Linux/unix系统下的各类服务器管理配置和维护；   3、具有大型IT系统、服务器、数据库运维经验者优先。具有网络工程师资格并取得资质证书优先；   4、熟悉网络监控软件，利用各种工具进行系统状态监控；   5、掌握MySQL、Redis、oracle等数据库基础知识；   6、熟练掌握python脚本语言使用；   7、具有较好的沟通表达能力，能在客户现场承担和客户、友商沟通汇报工作，有工作责任心和团队合作意识；   8、具有良好的方案编制能力，能独立输出部署实施方案   9、较强的抗压能力，适应经常出差。', '2019-03-20 11:12:06.000000', '99');
