INSERT INTO `post` VALUES ('1063', '数据库工程师', '薪资面议', '[合肥市蜀山区]稻香路88号', '两年以上工作经验', '本科学历', '招聘1人', '职位描述：   1、负责数据库（Oracle）日常操作、配置等工作；   2、快速处理解决数据库系统运行中出现的故障和问题，并能够制定和实施数据库优化方案；   3、负责提供数据库技术支持。      岗位要求：   1、计算机相关专业；2年及以上数据库相关项目工作经验；   2、熟悉oracle数据库的体系结构；有丰富的oracle数据库故障处理经验；   3、精通oracle的调优及操作系统的相关方面的调优；   4、熟悉Unix/Linux,oracle数据库OGG,RAC。并对服务器、存储有一定了解；   5、有管理和维护大型数据库的丰富经验；   6、较强的沟通、学习能力，良好的团队合作精神，稳定、有责任心；   7、有数据仓库和数据挖掘经历的优先考虑。', '2019-03-20 11:33:17.000000', '22');
