INSERT INTO `post` VALUES ('1083', '信息化技术员', '7000-10000元/月', '[合肥市高新区]安徽省合肥市高新区彩虹路418号', '两年以上工作经验', '本科学历', '招聘2人', '岗位职责：   1、负责MES异常问题点处理；   2、负责对接各部门提出的零星需求，跟进调研、评审、协助IT开发；   3、系统运维提升；   4、负责日常数据稽查；   5、负责信息化新项目跟进。      任职资格：   1、本科及以上学历   2、熟练使用WORD/EXCEL/PPT等常用办公软件；   3、有系统管理经验者优先；   4、具有良好的沟通协调能力及积极、认真、踏实的工作态度。', '2019-03-20 11:33:41.000000', '136');
